void FrameBufferSelect(uint32_t cpupage, uint32_t displaypage)
{
	// [31:2] unused
	// [1] CPU r/w page
	// [0] GPU scanout page
	*GPUCTL = ((cpupage&1)<<1) | (displaypage&1);
}

void ClearScreen(uint32_t bgcolor)
{
	for (int w=0;w<FRAME_HEIGHT*FRAME_WIDTH_IN_WORDS;++w)
		GPUFBWORD[w] = bgcolor;
}

void DrawTextLen(const int col, const int row, const int length, const char *message)
{
	int i = 0;
	uint32_t* residentfont32 = (uint32_t*)residentfont;
	while (i<length && message[i] != 0)
	{
		int currentchar = message[i]-32;
		if (currentchar<0)
		{
			++i;
			continue;
		}

		int charrow = (currentchar>>5)*8;
		int charcol = (currentchar%32)*2;

		for (int y=0;y<8;++y)
		{
			int py = ((y+row)*FRAME_WIDTH_IN_WORDS);
			for (int x=0; x<2; ++x) // 2 words per character (2x4 pixels)
			{
				uint32_t bits = residentfont32[64*(charrow+y) + charcol + x];
				GPUFBWORD[i*2+x+col+py] = bits;
			}
		}
		++i;
	}
}

uint32_t DrawText(const int col, const int row, const char *message)
{
	int i = 0;
	uint32_t* residentfont32 = (uint32_t*)residentfont;
	while (message[i] != 0)
	{
		int currentchar = message[i]-32;
		if (currentchar<0)
		{
			++i;
			continue;
		}

		int charrow = (currentchar>>5)*8;
		int charcol = (currentchar%32)*2;

		for (int y=0;y<8;++y)
		{
			int py = ((y+row)*FRAME_WIDTH_IN_WORDS);
			for (int x=0; x<2; ++x) // 2 words per character (2x4 pixels)
			{
				uint32_t bits = residentfont32[64*(charrow+y) + charcol + x];
				GPUFBWORD[i*2+x+col+py] = bits;
			}
		}
		++i;
	}

	return i;
}

uint32_t DrawDecimal(const int col, const int row, const int32_t i)
{
    const char digits[] = "0123456789";
    char msg[] = "                   ";

    int d = 1000000000;
    uint32_t enableappend = 0;
    uint32_t m = 0;
    if (i<0)
        msg[m++] = '-';
    for (int c=0;c<10;++c)
    {
        uint32_t r = ((i/d)%10)&0x7FFFFFFF;
        // Ignore preceeding zeros
        if ((r!=0) || enableappend || d==1)
        {
            enableappend = 1; // Rest of the digits can be appended
            msg[m++] = digits[r];
        }
        d = d/10;
    }
    msg[m] = 0;

    DrawText(col,row,msg);

	return m;
}


