void PrintDMA(const int col, const int row, const char *message, bool masked)
{
   int i=0;
   while (message[i] != 0)
   {
      int currentchar = message[i]-32;
      if (currentchar<0)
      {
         ++i;
         continue;
      }

      int charrow = (currentchar>>5)*8;
      int charcol = (currentchar%32)*8;

      for (int y=0;y<8;++y)
      {
         // Source address in SYSRAM (NOTE: The address has to be in multiples of DWORD)
         uint32_t sysramsource = uint32_t(font+(charcol+0+((charrow+y)<<8)))>>2;
         GPUFIFO[0] = GPUOPCODE(GPUSETREGISTER, 0, 4, GPU22BITIMM(sysramsource));
         GPUFIFO[1] = GPUOPCODE(GPUSETREGISTER, 4, 4, GPU10BITIMM(sysramsource));

         // Copy to top of the VRAM (Same rule here, address has to be in multiples of DWORD)
         int py = ((y+row)<<8);
         uint32_t vramramtarget = (i*8+0+col+py)>>2;
         GPUFIFO[2] = GPUOPCODE(GPUSETREGISTER, 0, 5, GPU22BITIMM(vramramtarget));
         GPUFIFO[3] = GPUOPCODE(GPUSETREGISTER, 5, 5, GPU10BITIMM(vramramtarget));

         // Length of copy in DWORDs
         uint32_t dmacount = 2;
         GPUFIFO[4] = GPUOPCODE(GPUSYSDMA, 4, 5, ((dmacount&0x3FFF) | (masked ? 0x4000 : 0x0))); // Zero-masked
      }
      ++i;
   }
}

void PrintDMA(const int col, const int row, const int maxlen, const char *message, bool masked)
{
   int i=0;
   while (i<maxlen && message[i] != 0)
   {
      int currentchar = message[i]-32;
      if (currentchar<0)
      {
         ++i;
         continue;
      }

      int charrow = (currentchar>>5)*8;
      int charcol = (currentchar%32)*8;

      for (int y=0;y<8;++y)
      {
         // Source address in SYSRAM (NOTE: The address has to be in multiples of DWORD)
         uint32_t sysramsource = uint32_t(font+(charcol+0+((charrow+y)<<8)))>>2;
         GPUFIFO[0] = GPUOPCODE(GPUSETREGISTER, 0, 4, GPU22BITIMM(sysramsource));
         GPUFIFO[1] = GPUOPCODE(GPUSETREGISTER, 4, 4, GPU10BITIMM(sysramsource));

         // Copy to top of the VRAM (Same rule here, address has to be in multiples of DWORD)
         int py = ((y+row)<<8);
         uint32_t vramramtarget = (i*8+0+col+py)>>2;
         GPUFIFO[2] = GPUOPCODE(GPUSETREGISTER, 0, 5, GPU22BITIMM(vramramtarget));
         GPUFIFO[3] = GPUOPCODE(GPUSETREGISTER, 5, 5, GPU10BITIMM(vramramtarget));

         // Length of copy in DWORDs
         uint32_t dmacount = 2;
         GPUFIFO[4] = GPUOPCODE(GPUSYSDMA, 4, 5, ((dmacount&0x3FFF) | (masked ? 0x4000 : 0x0)));
      }
      ++i;
   }
}

void Print(const int ox, const int oy, const char *message)
{
   int i=0;
   int charbyte, charrow, charcol, fontbyte;
   // Loop until we hit a null-terminator
   while (message[i] != 0)
   {
      charbyte = message[i]-32;
      charrow = (charbyte>>5)*8;
      charcol = (charbyte%32)*8;
      if(charbyte<0) // Skip non-printables
      {
         ++i;
         continue;
      }
      for (int y=0;y<8;++y)
      {
            int py = ((y+oy)<<8);
            for (int x=0;x<8;++x)
            {
               fontbyte = font[charcol+x + ((charrow+y)<<8)];
               //if (fontbyte)
                  VRAM[i*8+x+ox+py] = fontbyte;//0x00;
            }
      }
      ++i;
   }
}

